use 5.014;
use warnings;

my $_ = "
";#"AUGGCCAUGGCGCCCAGAACUGAGAUCAAUAGUACCCGUAUUAACGGGUGA";#"
#"AUGGCCAUGGCGCCCAGAACUGAGAUCAAUAGUACCCGUAUUAACGGGUGA";#"AAGUCG";
my $len = length $_;

my $i = 0;
my $protein; #Initialize it outside of loop otherwise my will scope it within the loop only
while($i <= $len) {
	my $substr = substr $_, $i, 3;
	$protein .= RNA2aa($substr);
	$i = $i+3; #Skip to every 3rd base on each iteration, this prevents sliding window method # QUESTION:: Doesn't each nucleotide get read? # it does # As in 2nd code
	}

say $protein;

my @pro = split " ",$protein;
#say $pro[0],"\t",$pro[1];
my $string = join "",@pro;

open OUT, ">Output_ch2_code1_translation.out" or die $!;
say OUT $string;


