														#push @f_protein, $f_protein ;
														#push @f_rna_template,$f_rna_template; #if $protein eq 'MA';
		
			
		}elsif (length $r_protein == $l_interest){
			
		push @{$r_hash_protein{$r_protein}}, $r_rna_template if $r_protein eq $interest;
		
		}																#$protein = "";
																		#$rna_template = "";
		$i = $i+3;								#0+3 = 3 => 0, 1, 2, 3 => 4th position			# if ($protein eq 'MA'){	
	}
	
	$j++;
		
}
	
	
				
		

	
		

