use strict;
use warnings;
use 5.014;

my $seq = "
";#"CGCCCGAATCCAGAACGCATTCCCATATTTCGGGACCACTGGCCTCCACGGTACGGACGTCAATCAAAT";
my $pat     = "TATCCTCA";#"ATTCTGGA";
my $len_in  = length $seq;
my $len_pat = length $pat;

my %posn_pattern;

for my $i ( 0 .. $len_in - $len_pat ) {

    #Extract k-mer of length = $len_pat from position $i
    my $substr = substr $seq, $i, $len_pat;

    #Save position/pattern if the extracted k-mer matches $pat by 3 (or 5 as in question) or fewer substitutions
    $posn_pattern{$i} = $substr if strDiffMaxDelta( $pat, $substr, 5 ); #$i is key and in this case represents the pattern position #substr is value
}

#say "$_ => $posn_pattern{$_}" for sort { $a <=> $b } keys %posn_pattern;
#say "$_\t" for sort { $a <=> $b } keys %posn_pattern;
# for (sort {$a <=> $b } keys %posn_pattern){
    # say join $_;
# }

open OUT, ">Output_fuzzy_matching_code6.out" or die $!;
say OUT join (" ", sort {$a <=> $b} keys %posn_pattern);


sub strDiffMaxDelta {
    my ( $s1, $s2, $maxDelta ) = @_;
    my $diffCount = () = ( $s1 ^ $s2 ) =~ /[^\x00]/g;
    return $diffCount <= $maxDelta;
}